window.requestAnimationFrame=window.requestAnimationFrame||window.mozRequestAnimationFrame||window.webkitRequestAnimationFrame||window.msRequestAnimationFrame;function countUp(e,c,a,b){var d=this;this.d=document.getElementById(e);this.dec=a*10||0;this.duration=b*1000||2000;this.startTime=null;this.frameVal=0;this.easeOutExpo=function(i,f,g,h){return g*(-Math.pow(2,-10*i/h)+1)+f};this.stepUp=function(g){if(d.startTime===null){d.startTime=g}var f=g-d.startTime;d.frameVal=d.easeOutExpo(f,0,c,d.duration);if(d.dec>0){d.frameVal=Math.round(d.frameVal*d.dec)/d.dec;d.frameVal=(d.frameVal>c)?c:d.frameVal}d.d.innerHTML=d.addCommas(d.frameVal.toFixed(a));if(f<d.duration){requestAnimationFrame(d.stepUp)}else{d.d.innerHTML=d.addCommas(c.toFixed(a))}};this.start=function(){if(!isNaN(c)&&c!==null){requestAnimationFrame(d.stepUp)}else{console.log("countUp error: endVal is not a number");d.d.innerHTML="--"}return false};this.reset=function(){this.d.innerHTML=0};this.addCommas=function(f){f+="";x=f.split(".");x1=x[0];x2=x.length>1?"."+x[1]:"";var g=/(\d+)(\d{3})/;while(g.test(x1)){x1=x1.replace(g,"$1,$2")}return x1+x2}};