INSERT INTO `omiyage` (`prod_id`, `prod_name`, `prod_main_img`, `prod_description`, `prod_price`, `change_point`, `type_id`) VALUES
(1, '原味雪茄蛋捲禮盒7入', 'eggroll.png', '蛋捲製作中採用的每<br>\n一顆蛋皆有產銷履歷，堅持\n每天使用新鮮雞蛋製作蛋捲，營養百分百保留，非市面上常用的冷藏蛋液，其濃醇內餡與餅皮交織的纏綿幸福，絕對翻轉\n你對蛋捲的印象！', 0, 580, 1),
(2, '經典鳳梨酥脆禮盒(6入裝)', 'pineapplecake.png', '烈日下熟成的鳳梨經過削切、熬煮，酥皮是用健康飼養的鮮力蛋，天然奶油和日本麵粉揉成，無任何人工合成的添加。', 0, 720, 1),
(3, '宜蘭三星蔥燒餅4入禮盒組', 'yilan.png', '宜蘭蔥燒餅製作精細，最大特色是香、酥、脆，手工製作，內餡包宜蘭蔥、胡椒、五香粉及少許的鹽提味，手工捍平後，內餡不能外露，最後再灑上芝麻，烘烤完成的燒餅又酥又脆。', 0, 450, 1),
(4, '南投埔里頂級真空香菇1包', 'driedmushroom.png', '100%本土農產菇，朵朵飽滿的香菇，萃取日月精華，這神奇的口感，足以撼動您的感官。', 0, 490, 1),
(5, '台中太陽堂奶油小酥餅禮盒(6入裝)', 'suncake.png', '酥脆的餅皮，軟 Q 而不甜膩的餡，人口即化，富有濃濃的奶油香! 讓您一口接著一口。', 0, 290, 1),
(6, '防潑水可收納漁夫遮陽帽 ', 'hat.png', '防潑水面料可遮陽防曬，其內裡採用透氣孔洞設計，配戴舒適，是戶外活動必備的一頂帽子。', 0, 880, 2),
(7, '旅行必備輕便行李箱', 'luggage.png', '採用PC材質，一體成型的設計，超輕量行李箱是旅途中最佳的好夥伴', 0, 2980, 2),
(8, '時尚環保運動水壺', 'bottle.png', '利用不銹鋼與海洋再生塑料材質製成產品，高質感設計同時兼具環保元素', 0, 1200, 2),
(9, '富士 Quick Snap 即可拍相機', 'camera.png', '一次性底片相機，讓你輕鬆拍出復古文青照，留下旅遊的美好回憶！', 0, 450, 2),
(10, '輕量皮革質感包', 'bag.png', '內部簡約一體成型設計，讓您輕鬆背負無煩惱。', 0, 390, 2),
(11, '北投享溫泉免費兩小時使用券', 'hotspring1.png', '享溫泉備有冷熱池，盡享泡湯之餘又能保有隱私，快放下手上工作與紛擾。', 0, 1330, 3),
(12, '台北經典買一送一SPA按摩券', 'spa.png', '提供全身SPA按摩，舒緩緊繃的肌肉，排除體內淤積的毒素，讓您感受優質按摩的療效', 0, 2000, 3),
(13, '台南很溫馨KTV三小時兌換卷', 'singing.png', 'KTV界的南霸天，讓您在三小時內唱好唱滿', 0, 750, 3),
(14, '新竹星空探秘豪華露營之旅(一晚)', 'camping1.png', '免裝備露營，省去搭帳篷的不便，營區群山環抱、雲霧繚繞，宛如置身雲中秘境', 0, 3500, 3),
(15, '宜蘭珍奶文化館手搖珍奶DIY體驗', 'bubbletea.png', '前往宜蘭珍奶文化館，一探台灣名產珍珠奶茶的製作祕辛！', 0, 250, 3),
(16, '天地堂人文茶館大杯珍珠奶茶兌換券', 'bubbletea1.png', '沒喝過珍奶等於沒到過台灣，世界珍奶發源地-天地堂，為您製做一杯香濃好喝的珍奶！', 0, 100, 4),
(17, '真鮮精緻海陸鍋物一人份', 'hotpot.png', '頂級食材提供最鮮美的好滋味，內用外帶都提供，讓客人品嘗到最優質的餐點！', 0, 500, 4),
(18, '嘉義道地小吃林聰明沙鍋魚頭', 'fish.png', '嘉義特產獨家口味．擁有三代傳承的好滋味，您不能錯過林聰明沙鍋魚頭。', 0, 400, 4),
(19, '美華酒店精緻下午茶一人份', 'afternoontea.png', '酒店內提供的每個蛋糕皆帶有精緻的口感，喜愛吃甜食的您絕對不要錯過。', 0, 1290, 4),
(20, '高雄林皇宮LV百匯甜點吃到飽', 'strawberry.png', '每個草莓蛋糕都有好幾顆大草莓被夾在厚厚的奶油餡裡，吃起來濃郁又爽口。', 0, 990, 4),
(21, '原木單柄琺瑯牛奶鍋', 'pot.png', '硬質琺瑯塗層，不易掉色，同時擁有高抗酸鹼性及卓越的保溫性能。', 0, 990, 5),
(22, '大容量旅行收納背包(活力橘)', 'backpack.png', '大容量設計，背包內的夾層多，收納一目了然。', 0, 600, 5),
(23, '愛爾浦不銹鋼水壺(霧黑)', 'kettle.png', '快速煮沸、容易清潔 ，自動斷電安全系統。', 0, 1200, 5),
(24, '阿姆龍藍牙傳輸體重體脂計(白色)', 'scale.png', '消費者理想品牌，精準測量體脂肪率及基礎代謝。', 0, 1800, 5),
(25, '簡便瑜珈墊組( 瑜珈墊/瑜珈背帶/瑜珈袋)', 'yoga.png', '台灣獨家配色，瑜珈墊品質好、防滑度高。', 0, 450, 5);
